package modelos;public abstract class Producto implements Comprable {	private String nombre;	private Integer precio;	private Integer stock;	private String descripcion;	private String id;	protected Producto(String nombre, Integer precio, Integer stock, String descripcion) {		this.nombre = nombre;		this.precio = precio;		this.stock = stock;		this.descripcion = descripcion;		this.id = generarId();	}	public String getNombre() {		return nombre;	}	public void setNombre(String nombre) {		this.nombre = nombre;	}	public Integer getPrecio() {		return precio;	}	public void setPrecio(Integer precio) {		this.precio = precio;	}	public Integer getStock() {		return stock;	}	public void setStock(Integer stock) {		this.stock = stock;	}	public String getDescripcion() {		return descripcion;	}	public void setDescripcion(String descripcion) {		this.descripcion = descripcion;	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	@Override	public Double calcularPrecio(Double descuento, Double impuesto){		return this.precio - (this.precio*(descuento/100)) + (this.precio*(impuesto/100));	}	private String generarId(){		int numberId = (int) (1 + Math.random() * 9999);		return String.valueOf(numberId);	}}