package sistemadeturnos;import java.util.ArrayList;import java.util.Scanner;public class App {	public static void main(String[] args) {	ArrayList<Integer> turnos = new ArrayList<>();int opcion;	try(Scanner scanner = new Scanner(System.in)){	do {		menu();		opcion = scanner.nextInt();		switch (opcion){			case 1 -> {				System.out.println("Nuevo  turno generado");				turnos.add(1);			}			case 2 -> {}			case 3 -> {}			case 4 -> {}			default -> System.out.println("Opción inválida");		}	} while (opcion!=4);	} catch (Exception e) {		throw new RuntimeException(e);	}	}	public static void menu(){		System.out.println("MENÚ DE TURNOS");		System.out.println("1- Nuevo Cliente");		System.out.println("2- Llamar siguiente");		System.out.println("3- Llamar específico");		System.out.println("4- Salir");	}}